import spock.lang.Specification
import spock.lang.Unroll

class SherlockAndTheValidStringSpec extends Specification {

    void setup(){

    }

    @Unroll
    void 'Validate Sherloc and the Valid String' (){
        when:
        String ans = SherlockAndTheValidString.isValid(s)
        then:
        ans == res
        where:
        s                   | res
        'aabbcd'            | SherlockAndTheValidString.NO
        'aabbccddeefghi'    | SherlockAndTheValidString.NO
        'abcdefghhgfedecba' | SherlockAndTheValidString.YES
        'abbac'             | SherlockAndTheValidString.YES
        'aabbc'             | SherlockAndTheValidString.YES
        '' | SherlockAndTheValidString.YES
    }
}
